* Classpathï¼Œ-cp|classpathå‚æ•°è§£æå®ç°çš„å…¥å£æ–¹æ³•
{% codeblock lang:golang %}
type Classpath struct { //ClasspathåŒ…å«javaçš„ä¸‰ç§åŸºç¡€å¯åŠ¨è·¯å¾„
	bootClasspath Entry 
	extClasspath Entry
	userClasspath Entry
}

func Parse(jreOption, cpOption string) * Classpath  {
	cp := &Classpath{} //å®ä¾‹åŒ–Classpathå¯¹è±¡
	cp.parseBootAndExtClasspath(jreOption) //æŒ‰ç…§-Xjreå‚æ•°è§£æBootå’ŒExtå¯åŠ¨è·¯å¾„
	cp.parseUserClasspath(cpOption) //æŒ‰ç…§-cp|classpathå‚æ•°è§£æUserå¯åŠ¨è·¯å¾„
	return cp //è¿”å›è®¾ç½®å¥½å¯åŠ¨è·¯å¾„çš„Classpathå¯¹è±¡
}

func (self *Classpath) ReadClass(className string) ([]byte,Entry,error){
	className = className + ".class" //åˆå§‹åŒ–classNameæ–‡ä»¶å
	if data, entry, err := self.bootClasspath.readClass(className); err == nil { //å…ˆæ‰«æBootå¯åŠ¨è·¯å¾„ä¸‹æ˜¯å¦å­˜åœ¨è¯¥className.classæ–‡ä»¶
	   return data, entry, err //å­˜åœ¨åˆ™è¿”å›
	}
	if data, entry, err := self.extClasspath.readClass(className); err == nil { //å†æ‰«æExtå¯åŠ¨è·¯å¾„ä¸‹æ˜¯å¦å­˜åœ¨è¯¥className.classæ–‡ä»¶
	   return data, entry, err //å­˜åœ¨åˆ™è¿”å›
	}
	return self.userClasspath.readClass(className) //ä»¥ä¸Šä¸¤ä¸ªè·¯å¾„ä¸‹éƒ½ä¸å­˜åœ¨ï¼Œåˆ™æ‰«æUserå¯åŠ¨è·¯å¾„
}

func (self *Classpath) String() string  {
	return self.userClasspath.String()
}

func (self *Classpath) parseBootAndExtClasspath(jreOption string) { //æŒ‰ç…§-Xjreå‚æ•°è§£æBootå’ŒExtå¯åŠ¨è·¯å¾„
	jreDir := getJreDir(jreOption) //è·å–jreè·¯å¾„
   // jre/lib/*
   jreLibPath := filepath.Join(jreDir, "lib", "*")
   self.bootClasspath = newWildcardEntry(jreLibPath)
   // jre/lib/ext/*
   jreExtPath := filepath.Join(jreDir, "lib", "ext", "*")
   self.extClasspath = newWildcardEntry(jreExtPath)
}

func getJreDir(jreOption string) string {
	if jreOption != "" && exists(jreOption) { //jreOptionä¸ä¸ºç©ºä¸”å­˜åœ¨æ—¶è¿”å›è¯¥è·¯å¾„
	   return jreOption
	}
	if exists("./jre") { //./jreè·¯å¾„å­˜åœ¨æ—¶ï¼Œè¿”å›.jre
	   return "./jre"
	}
	if jh := os.Getenv("JAVA_HOME"); jh != "" { //ç¯å¢ƒå˜é‡JAVA_HOMEå­˜åœ¨å¹¶ä¸”ä¸ä¸ºç©ºæ—¶
	   return filepath.Join(jh, "jre") //è¿”å›JAVA_HOME + "jre"
	}
	panic("Can not find jre folder!")
 }

 func exists(path string) bool {
	if _, err := os.Stat(path); err != nil {
	   if os.IsNotExist(err) {
		  return false
	   }
	}
	return true
 }

 func (self *Classpath) parseUserClasspath(cpOption string) { //æŒ‰ç…§-cp|classpathå‚æ•°è§£æUserå¯åŠ¨å‚æ•°
	if cpOption == "" { //cpOptionä¸ºç©ºæ—¶
	   cpOption = "." //è¿”å›ç›¸å¯¹è·¯åŠ²
	}
	self.userClasspath = newEntry(cpOption) //æ ¹æ®-cp|classpathå‚æ•°å®ä¾‹åŒ–å¯¹åº”çš„Entry
 }
